mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT a.clubApplyIdx, v.volunIdx AS volunIdx, s.serviceName, subject, v.startDay, v.endDay, a.enabled, progress, place, volunteer_type, v.volunDays, v.typeIdx1, v.typeIdx2, ( (SELECT NVL(COUNT(*), 0) FROM club_apply_memlist c JOIN club_apply a ON c.clubApplyIdx=a.clubApplyIdx WHERE a.volunIdx = v.volunIdx)+ ( SELECT NVL(COUNT(*), 0) FROM volun_bbs_memlist vb WHERE vb.volunIdx=v.volunIdx))AS applyNum, ( select sum(maxNum) from MEMNUMPERDATE mnpd where mnpd.volunIdx=v.volunIdx ) as maxNum FROM volun_bbs v JOIN club_apply a ON v.volunIdx=a.volunIdx JOIN serviceInfo s ON v.serviceIdx=s.serviceIdx
WHERE a.enabled=1 ORDER BY volunIdx DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}
