mapper.select:
		SELECT * FROM (
	    SELECT ROWNUM rnum, tb.* FROM (
	      SELECT f.clubfreeIdx, f.userIdx,f.clubIdx, userName, subject,
	      (SELECT NVL(COUNT(*), 0) FROM club_free_reply_${clubSeq} WHERE clubFreeIdx=1 AND answer=0) replyDataCount,
	      TO_CHAR(f.created,'YYYY-MM-DD' ) created, f.content, hitCount
	      FROM club_free_${clubSeq} f 
	      JOIN memberInfo m ON f.userIdx = m.userIdx
	      
	        
				      
			    
		    
		    ORDER BY f.clubfreeIdx DESC
	  
	     ) tb WHERE ROWNUM <= #{end}
	  ) WHERE rnum >= #{start}
