mapper.insert:
INSERT INTO club_notice(clubnoticeIdx, userIdx, clubIdx,subject, content, saveFilename, originalFilename)
	VALUES(club_seq.NEXTVAL, #{userIdx}, '4', #{subject}
				,#{content}, #{saveFilename, jdbcType=VARCHAR}, #{originalFilename,jdbcType=VARCHAR})

mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT clubnoticeIdx, m.userIdx, userId, m.userName, subject
			           ,TO_CHAR(n.created, 'YYYY-MM-DD') created
					   ,hitCount, n.saveFilename
			        FROM club_notice n JOIN memberInfo m ON n.userIdx=m.userIdx
                    
                         
			                  
		                 
	                
	                ORDER BY clubnoticeIdx DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
