mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT a.clubApplyIdx, v.volunIdx AS volunIdx, s.serviceName, subject, v.startDay, v.endDay, a.enabled, progress, place, volunteer_type, v.volunDays, NVL(replyCount, 0) replyCount, ( (SELECT NVL(COUNT(*), 0) FROM club_apply_memlist c JOIN club_apply a ON c.clubApplyIdx=a.clubApplyIdx WHERE a.volunIdx = v.volunIdx)+ ( SELECT NVL(COUNT(*), 0) FROM volun_bbs_memlist vb WHERE vb.volunIdx=v.volunIdx))AS applyNum, ( select sum(maxNum) from MEMNUMPERDATE mnpd where mnpd.volunIdx=v.volunIdx ) as maxNum
FROM volun_bbs v JOIN club_apply a ON v.volunIdx=a.volunIdx JOIN serviceInfo s ON v.serviceIdx=s.serviceIdx LEFT OUTER JOIN ( SELECT clubApplyIdx, count(*) replycount FROM club_applyR tbreply GROUP BY clubApplyIdx ) tbreply ON a.clubApplyIdx = tbreply.clubApplyIdx
AND a.enabled=1
where a.enabled=1
ORDER BY volunIdx DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

mapper.delete:
 
		DELETE club_applyR WHERE
	    
			 clubApplyIdx=#{num}
		
	    
			 CARIDX IN 
               (SELECT CARIDX FROM club_applyR START WITH CARIDX = #{replyNum}
	            CONNECT BY PRIOR CARIDX = answer)
		
	    
			 CARIDX=#{replyNum}

mapper.select:
SELECT  NVL(COUNT(*), 0) AS likeCount
 FROM club_applyR_like
 WHERE CARIdx=#{replyNum}

SELECT userIdx , LISTAGG(hopeDate,',')WITHIN GROUP (ORDER BY userIdx) hopedate
FROM club_apply_memlist WHERE clubApplyIdx=#{clubApplyIdx}
GROUP BY userIdx;
