mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT tbrev.clubReviewIdx, tbrev.useridx, userid, username, subject,tbrev.content ,created,hitcount,NVL(replyCount, 0) replyCount,NVL(likeCount, 0) likeCount FROM ( ( SELECT clubReviewIdx, m.userIdx, m.userId, m.userName, subject,s.content ,TO_CHAR(s.created, 'YYYY-MM-DD') created ,hitCount FROM club_review s JOIN checkId m ON s.useridx=m.useridx
)tbrev LEFT OUTER JOIN ( SELECT clubReviewIdx, count(*) replycount FROM club_reviewr tbreply GROUP BY clubReviewIdx ) tbreply ON tbrev.clubReviewIdx = tbreply.clubReviewIdx ) LEFT OUTER JOIN ( SELECT clubReviewIdx, COUNT(*) likeCount FROM club_review_like GROUP BY clubReviewIdx ) tblike ON tbrev.clubReviewIdx = tblike.clubReviewIdx
ORDER BY tbrev.clubReviewIdx DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

mapper.update:
UPDATE club_review_file SET originalFilename=#{originalFilename, jdbcType=VARCHAR}, 
saveFilename=#{saveFilename, jdbcType=VARCHAR}
           WHERE clubReviewIdx = #{clubReviewIdx}

mapper.delete:
	DELETE club_review_reply WHERE
	    
			 clubReviewIdx=#{num}
		
	    
			 SRRIDX IN 
               (SELECT SRRIDX FROM club_review_reply START WITH  SRRIDX = #{replyNum}
	            CONNECT BY PRIOR SRRIDX = answer)
		
	    
			 SRRIDX=#{replyNum}
