mapper.select:
		 SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
		       SELECT sguestIdx, g.useridx, CONTENT, created,userName
		               FROM service_guest g JOIN memberInfo m
                   ON g.useridx=m.useridx
		               ORDER BY sguestIdx DESC
		               
		    ) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}
