mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT serviceReviewIdx, m.userIdx,userId, userName, subject
			           ,TO_CHAR(s.created, 'YYYY-MM-DD') created
					   ,hitCount
			        FROM service_review s
	       			 JOIN memberInfo m
	       			 ON s.userIdx=m.userIdx
                    
                         
			                  
		                 
	                
	                ORDER BY serviceReviewIdx DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}

mapper.update:
UPDATE service_review_file SET originalFilename=#{originalFilename, jdbcType=VARCHAR}, 
saveFilename=#{saveFilename, jdbcType=VARCHAR}
           WHERE serviceReviewIdx = #{serviceReviewIdx}
