mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT tbrev.servicereviewidx, tbrev.useridx,userid, username, subject ,created,hitcount,NVL(replyCount, 0) replyCount,NVL(likeCount, 0) likeCount FROM ( ( SELECT serviceReviewIdx, m.userIdx,userId, userName, subject ,TO_CHAR(s.created, 'YYYY-MM-DD') created ,hitCount FROM service_review s JOIN memberInfo m ON s.userIdx=m.userIdx JOIN checkId c ON m.useridx=c.useridx
)tbrev LEFT OUTER JOIN ( SELECT serviceReviewIdx, count(*) replycount FROM service_review_reply tbreply GROUP BY serviceReviewIdx ) tbreply ON tbrev.serviceReviewIdx = tbreply.serviceReviewIdx ) LEFT OUTER JOIN ( SELECT servicereviewidx, COUNT(*) likeCount FROM service_review_like GROUP BY servicereviewidx ) tblike ON tbreply.servicereviewidx = tblike.servicereviewidx
ORDER BY serviceReviewIdx DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

mapper.update:
UPDATE service_review_file SET originalFilename=#{originalFilename, jdbcType=VARCHAR}, 
saveFilename=#{saveFilename, jdbcType=VARCHAR}
           WHERE serviceReviewIdx = #{serviceReviewIdx}

mapper.delete:
	DELETE service_review_reply WHERE
	    
			 serviceReviewIdx=#{num}
		
	    
			 SRRIDX IN 
               (SELECT SRRIDX FROM service_review_reply START WITH  SRRIDX = #{replyNum}
	            CONNECT BY PRIOR SRRIDX = answer)
		
	    
			 SRRIDX=#{replyNum}
