mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			      SELECT sqnaIdx, s.useridx,userName,subject, CONTENT, hitCount,to_char(created,'YYYY-MM-DD') created
				  FROM service_qna s join checkId c on s.userIdx=c.userIdx 
                    
	     	           
	     	               AND (subject LIKE '%' || #{searchValue} || '%' OR
	     	               content LIKE '%' || #{searchValue} || '%')
	     	           
	                
	                ORDER BY sqnaIdx DESC
	
	        ) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}

      SELECT sqnaIdx, s.useridx,userId,userName, subject,created,CONTENT, hitCount
			FROM service_qna s join checkId c on s.userIdx=c.userIdx
			WHERE sqnaIdx = #{sqnaIdx}
