mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			      SELECT sqnaIdx, s.useridx,c.userId,userName,subject, CONTENT, hitCount,to_char(created,'YYYY-MM-DD') created
			      ,answer,groupNum
				  FROM service_qna s join checkId c on s.userIdx=c.userIdx 
                    
	     	           
	     	               
	     	           
	                
	                 ORDER BY groupNum DESC,sqnaIdx ASC
	
	        ) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}

      SELECT sqnaIdx, s.useridx,userId,userName, subject,created,CONTENT, hitCount
			FROM service_qna s join checkId c on s.userIdx=c.userIdx
			WHERE sqnaIdx = #{sqnaIdx}
