mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			      SELECT qnaIdx, s.useridx,c.userId,userName,subject, CONTENT, hitCount,to_char(created,'YYYY-MM-DD') created
			      ,answer,groupNum,quserIdx
				  FROM qna q join checkId c on q.userIdx=c.userIdx 
                    
	     	           
	     	               
	     	           
	                
	                 ORDER BY groupNum DESC,qnaIdx ASC
	
	        ) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}

      SELECT qnaIdx, s.useridx,userId,userName, subject,created,CONTENT, hitCount,answer,groupNum
			FROM qna q join checkId c on q.userIdx=c.userIdx
			WHERE qnaIdx = #{qnaIdx}
